import Item1 from "../../images/item1.jpg";
import Item2 from "../../images/item2.jpg";
import Item3 from "../../images/item3.jpg";
import Item4 from "../../images/item4.jpg";
import Item5 from "../../images/item5.jpg";
import Item6 from "../../images/item6.jpg";
import {
  CHANGE_FILTER,
  ADD_PIZZA,
  EDIT_PIZZA,
  DELETE_PIZZA,
  ON_INIT_FUNCTION,
} from "../actions/action-types/cart-actions";

const initState = {
  items: [
    {
      id: 1,
      activeDiscount: true,
      discount: 5,
      title: "Pizza1",
      desc: "we just having nice pizza with peproni and cheese",
      price: 110,
      img: Item1,
      dep: "main course",
    },
    {
      id: 2,
      activeDiscount: false,
      title: "adidas",
      discount: 10,
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 80,
      img: Item2,
      dep: "main course",
    },
    {
      id: 3,
      activeDiscount: true,
      discount: 10,
      title: "vans",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 120,
      img: Item3,
      dep: "side course",
    },
    {
      id: 4,
      activeDiscount: false,
      discount: 10,
      title: "white",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 260,
      img: Item4,
      dep: "side course",
    },
    {
      id: 5,
      activeDiscount: true,
      discount: 10,
      title: "cropped-sho",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 160,
      img: Item5,
      dep: "side course",
    },
    {
      id: 6,
      activeDiscount: false,
      discount: 10,
      title: "blues3",
      desc:
        "Lorem ipsum dolor sit amet  abc consectetur adipisicing elit. Minima, ex.",
      price: 90,
      img: Item6,
      dep: "main course",
    },
    {
      id: 7,
      activeDiscount: false,
      discount: 10,
      title: "bluesss2",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 90,
      img: Item6,
      dep: "side course",
    },
  ],
  addedItems: [],
  fitlteredItems: [],
  total: 0,
};
const cartReducer = (state = initState, action) => {
  switch (action.type) {
    case ON_INIT_FUNCTION:
      return {
        ...state,
        items: state.items,
      };

    case CHANGE_FILTER:
      return {
        ...state,
        fitlteredItems: action.filteredProducts,
      };
    case ADD_PIZZA: {
      return {
        ...state,
        items: action.items,
      };
    }
    case EDIT_PIZZA: {
      return {
        ...state,
        items: action.items,
      };
    }
    case DELETE_PIZZA: {
      return { ...state, items: action.items };
    }

    default:
      return state;
  }
};

export default cartReducer;
