import {
  ADD_TO_CART,
  REMOVE_ITEM,
  SUB_QUANTITY,
  ADD_QUANTITY,
  ADD_SHIPPING,
  CHANGE_FILTER,
  ON_INIT_FUNCTION,
} from "./action-types/cart-actions";

//add cart action
export const initFunction = () => {
  return async (dispatch, getState) => {
    const state = getState();

    dispatch({
      type: ON_INIT_FUNCTION,
    });
  };
};
export const changeFilter = (value, selectedValue) => {
  return async (dispatch, getState) => {
    const state = getState();
    let filteredProducts = [];

    if (value.length > 0 || selectedValue.length > 0) {
      filteredProducts = state.items.filter((each) => {
        if (selectedValue !== "select_Department") {
          return each.title.includes(value) && each.dep.includes(selectedValue);
        } else {
          return each.title.includes(value);
        }
      });
    } else {
      filteredProducts = state.items;
    }

    dispatch({
      type: CHANGE_FILTER,
      value,
      selectedValue,
    });
  };
};
export const addToCart = (id) => {
  return {
    type: ADD_TO_CART,
    id,
  };
};
//remove item action
export const removeItem = (id) => {
  return {
    type: REMOVE_ITEM,
    id,
  };
};
//subtract qt action
export const subtractQuantity = (id) => {
  return {
    type: SUB_QUANTITY,
    id,
  };
};
//add qt action
export const addQuantity = (id) => {
  return {
    type: ADD_QUANTITY,
    id,
  };
};
