import React, { Component } from "react";
import { connect } from "react-redux";
// import { addToCart, changeFilter } from "./actions/cartActions";
import * as actions from "./actions/cartActions";
import Pagination from "./Pagination";
class Home extends Component {
  constructor(props) {
    super(props);
    var exampleItems = [...Array(150).keys()].map((i) => ({
      id: i + 1,
      name: "Item " + (i + 1),
    }));

    this.state = {
      inputValue: "",
      depSelectedValue: "",
      exampleItems: exampleItems,
      pageOfItems: [],
    };
    this.onChangePage = this.onChangePage.bind(this);
  }
  onChangePage(pageOfItems) {
    // update state with new page of items
    this.setState({ pageOfItems: pageOfItems });
  }
  componentDidMount() {
    this.props.onInitFunction("");
  }
  handleClick = (id) => {
    this.props.addToCart(id);
  };
  handleDepChange = (value) => {
    // console.log(value, "value");
    let inputValuee = value.target.value;
    this.setState({ depSelectedValue: inputValuee });

    this.props.onChangeFilter(this.state.inputValue, inputValuee);
  };
  handleInputChange = (value) => {
    // console.log(value, "value");
    let inputValueq = value.target.value;
    this.setState({ inputValue: inputValueq });

    this.props.onChangeFilter(inputValueq, this.state.depSelectedValue);
  };
  render() {
    let allItems =
      // this.props.fitlteredItems && this.props.fitlteredItems.length > 0
      (this.state.depSelectedValue && this.state.depSelectedValue.length > 0) ||
      (this.state.inputValue && this.state.inputValue.length > 0)
        ? this.props.fitlteredItems
        : this.props.items;

    let itemList = allItems.map((item) => {
      return (
        <div className="card" key={item.id}>
          <div className="card-image">
            <img src={item.img} alt={item.title} />
            <span className="card-title">{item.title}</span>
            <span
              to="/"
              className="btn-floating halfway-fab waves-effect waves-light red"
              onClick={() => {
                this.handleClick(item.id);
              }}
            >
              <i className="material-icons">add</i>
            </span>
          </div>

          <div className="card-content">
            <p>{item.desc}</p>
            <p>
              <b>
                Price:{" "}
                {item.activeDiscount ? (
                  <span>
                    <span
                      style={{
                        textDecorationLine: "line-through",
                        textDecorationStyle: "solid",
                      }}
                    >
                      {item.price}$
                    </span>
                    =>
                    {Math.round(item.price) -
                      (item.price * item.discount) / 100}
                    $
                  </span>
                ) : (
                  item.price + "$"
                )}
              </b>
            </p>
          </div>
        </div>
      );
    });

    return (
      <div className="container">
        <div className="search">
          <div className="searchFor">
            <input
              className="input_field"
              type="text"
              placeholder="search For Product"
              onChange={this.handleInputChange}
              value={this.state.inputValue}
            />
          </div>
          <div>
            <select
              className="input_field"
              onChange={this.handleDepChange}
              value={this.state.depSelectedValue}
            >
              <option value="select_Department">Select a Department</option>
              <option value="cars">cars</option>
              <option value="shoes">shoes</option>
            </select>
          </div>
        </div>
        <h3 className="center">Our items</h3>
        <div className="box">{itemList}</div>
        <div>
          <div className="container">
            <div className="text-center">
              <h1>React - Pagination Example with logic like Google</h1>
              {this.state.pageOfItems.map((item) => (
                <div key={item.id}>{item.name}</div>
              ))}
              <Pagination items={itemList} onChangePage={this.onChangePage} />
            </div>
          </div>
          <hr />
          <div className="credits text-center">
            <p>
              <a
                href="http://jasonwatmore.com/post/2017/03/14/react-pagination-example-with-logic-like-google"
                target="_top"
              >
                React - Pagination Example with Logic like Google
              </a>
            </p>
            <p>
              <a href="http://jasonwatmore.com" target="_top">
                JasonWatmore.com
              </a>
            </p>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  // console.log(state, "state");
  return {
    items: state.items,
    fitlteredItems: state.fitlteredItems,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    onInitFunction: (mainObject) => dispatch(actions.initFunction(mainObject)),
    addToCart: (id) => {
      dispatch(actions.addToCart(id));
    },
    onChangeFilter: (inputValue, selectedValue) =>
      dispatch(actions.changeFilter(inputValue, selectedValue)),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Home);
