import Item1 from "../../images/item1.jpg";
import Item2 from "../../images/item2.jpg";
import Item3 from "../../images/item3.jpg";
import Item4 from "../../images/item4.jpg";
import Item5 from "../../images/item5.jpg";
import Item6 from "../../images/item6.jpg";
import {
  ADD_TO_CART,
  REMOVE_ITEM,
  SUB_QUANTITY,
  ADD_QUANTITY,
  ADD_SHIPPING,
  SUB_SHIPPING,
  CHANGE_FILTER,
  ON_INIT_FUNCTION,
} from "../actions/action-types/cart-actions";

const initState = {
  fitlteredItems: [
    {
      id: 1,
      activeDiscount: true,
      discount: 5,
      title: "winter body",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 110,
      img: Item1,
      dep: "cars",
    },
    {
      id: 2,
      activeDiscount: false,
      title: "adidas",
      discount: 10,
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 80,
      img: Item2,
      dep: "shoes",
    },
    {
      id: 3,
      activeDiscount: true,
      discount: 10,
      title: "vans",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 120,
      img: Item3,
      dep: "cars",
    },
    {
      id: 4,
      activeDiscount: false,
      discount: 10,
      title: "white",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 260,
      img: Item4,
      dep: "shoes",
    },
    {
      id: 5,
      activeDiscount: true,
      discount: 10,
      title: "cropped-sho",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 160,
      img: Item5,
      dep: "cars",
    },
    {
      id: 6,
      activeDiscount: false,
      discount: 10,
      title: "blues3",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 90,
      img: Item6,
      dep: "shoes",
    },
    {
      id: 7,
      activeDiscount: false,
      discount: 10,
      title: "bluesss2",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 90,
      img: Item6,
      dep: "shoes",
    },
    {
      id: 8,
      activeDiscount: false,
      discount: 10,
      title: "bluesss1",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 90,
      img: Item6,
      dep: "cars",
    },
  ],
  addedItems: [],
  fitlteredItems: [],
  total: 0,
};
const cartReducer = (state = initState, action) => {
  switch (action.type) {
    case ON_INIT_FUNCTION:
      return {
        ...state,
        items: state.items,
      };

    case CHANGE_FILTER:
      return {
        ...state,
        fitlteredItems: action.filteredProducts,
      };

    //INSIDE HOME COMPONENT
    case ADD_TO_CART:
      return {
        ...state,
        addedItems: action.addedItems,
        total: action.total,
      };

    case REMOVE_ITEM:
      return {
        ...state,
        addedItems: action.addedItems,
        total: action.total,
      };

    case ADD_QUANTITY:
      return {
        ...state,
        total: action.newTotal,
      };
    case SUB_QUANTITY:
      return {
        ...state,
        addedItems: action.addedItems,
        total: action.addedtotal,
      };

    case ADD_SHIPPING:
      return {
        ...state,
        total: state.total + 6,
      };

    case SUB_SHIPPING:
      return {
        ...state,
        total: state.total - 6,
      };

    default:
      return state;
  }
};

export default cartReducer;
