import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import "./Cart.css";
import * as Actions from "../actions/cartActions";

class Cart extends Component {
  constructor() {
    super();
    this.state = {
      id: "",
      type: "",
      name: "",
      price: "",
      photo: "",
    };
    this.handelChange = this.handelChange.bind(this);
    this.handleChangeImage = this.handleChangeImage.bind(this);
  }

  componentDidMount() {
    if (typeof this.props.match.params.id == "undefined") {
    } else {
      const editedId = this.props.match.params.id;

      let editableItem = this.props.items.find((item) => item.id == editedId);

      this.setState({
        id: editedId,
        type: editableItem.dep,
        name: editableItem.title,
        price: editableItem.price,
        img: editableItem.photo,
      });
    }
  }
  handleChangeImage(event) {
    this.setState({
      img: URL.createObjectURL(event.target.files[0]),
    });
  }
  handelChange(e) {
    let change = {};
    change[e.target.name] = e.target.value;
    console.log(change, "change");
    this.setState(change);
  }
  render() {
    return (
      <div className="container">
        <form
          onSubmit={(e) => {
            // console.log(this.state, "this.state");
            e.preventDefault();
            this.state.id > 0
              ? this.props.editPizza(this.state)
              : this.props.addPizza(this.state);

            this.props.history.push("/");
          }}
        >
          <h3>Add Pizza</h3>
          <center>
            <h6 style={{ color: "red" }}>{this.props.errorMessage}</h6>
          </center>

          <input type="hidden" name="id" value={this.state.id} />

          <div className="form-group">
            <div className="mainFormDiv">
              <div className="formTitle">
                <label>Type</label>
              </div>
              <div className="formInput">
                <select
                  name="type"
                  onChange={this.handelChange}
                  value={this.state.type}
                >
                  <option value="select_Department">Select Course Type</option>
                  <option value="main course">main course</option>
                  <option value="side course">side couse</option>
                </select>
              </div>
            </div>
          </div>
          <div className="mainFormDiv">
            <div className="formTitle">
              <label>Name</label>
            </div>
            <div className="formInput">
              <input
                name="name"
                placeholder="pizza name"
                value={this.state.name}
                onChange={this.handelChange}
              />
            </div>
          </div>

          <div className="mainFormDiv">
            <div className="formTitle">
              <label>Price</label>
            </div>
            <div className="formInput">
              <input
                name="price"
                placeholder="Price"
                value={this.state.price}
                onChange={this.handelChange}
              />
            </div>
          </div>
          <div className="mainFormDiv">
            <div className="formTitle">
              <label>Photo</label>
            </div>
            <div className="formInput">
              <input type="file" onChange={this.handleChangeImage} />
              <img src={this.state.img} width="200px;" />
            </div>
          </div>

          <div className="mainFormDiv">
            <button type="submit" className="waves-effect waves-light btn">
              {this.state.id > 0 ? "Update" : "Add"}
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    items: state.items,
    //addedItems: state.addedItems
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    addPizza: (formValues) => {
      dispatch(Actions.addPizza(formValues));
    },
    editPizza: (formValues) => {
      dispatch(Actions.editPizza(formValues));
    },
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Cart);
