import Item1 from "../../images/item1.jpg";
import Item2 from "../../images/item2.jpg";
import Item3 from "../../images/item3.jpg";
import Item4 from "../../images/item4.jpg";
import Item5 from "../../images/item5.jpg";
import Item6 from "../../images/item6.jpg";
import {
  ADD_TO_CART,
  REMOVE_ITEM,
  SUB_QUANTITY,
  ADD_QUANTITY,
  ADD_SHIPPING,
  CHANGE_FILTER,
  ON_INIT_FUNCTION,
} from "../actions/action-types/cart-actions";

const initState = {
  items: [
    {
      id: 1,
      activeDiscount: true,
      discount: 5,
      title: "winter body",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 110,
      img: Item1,
      dep: "cars",
    },
    {
      id: 2,
      activeDiscount: false,
      title: "adidas",
      discount: 10,
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 80,
      img: Item2,
      dep: "shoes",
    },
    {
      id: 3,
      activeDiscount: true,
      discount: 10,
      title: "vans",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 120,
      img: Item3,
      dep: "cars",
    },
    {
      id: 4,
      activeDiscount: false,
      discount: 10,
      title: "white",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 260,
      img: Item4,
      dep: "shoes",
    },
    {
      id: 5,
      activeDiscount: true,
      discount: 10,
      title: "cropped-sho",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 160,
      img: Item5,
      dep: "cars",
    },
    {
      id: 6,
      activeDiscount: false,
      discount: 10,
      title: "blues3",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 90,
      img: Item6,
      dep: "shoes",
    },
    {
      id: 7,
      activeDiscount: false,
      discount: 10,
      title: "bluesss2",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 90,
      img: Item6,
      dep: "shoes",
    },
    {
      id: 8,
      activeDiscount: false,
      discount: 10,
      title: "bluesss1",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 90,
      img: Item6,
      dep: "cars",
    },
  ],
  addedItems: [],
  fitlteredItems: [],
  total: 0,
};
const cartReducer = (state = initState, action) => {
  if (action.type === ON_INIT_FUNCTION) {
    return {
      ...state,
      items: state.items,
    };
  }
  if (action.type === CHANGE_FILTER) {
    return {
      ...state,
      fitlteredItems: action.filteredProducts,
    };
  }
  //INSIDE HOME COMPONENT
  if (action.type === ADD_TO_CART) {
    // let addedItem = state.items.find((item) => item.id === action.id);
    // //check if the action id exists in the addedItems
    // let existed_item = state.addedItems.find((item) => action.id === item.id);
    // let priceAfterDiscount = addedItem.price;
    // if (addedItem.activeDiscount) {
    //   priceAfterDiscount =
    //     addedItem.price - (addedItem.price * addedItem.discount) / 100;
    // }
    // if (existed_item) {
    //   addedItem.quantity += 1;
    //   return {
    //     ...state,
    //     total: state.total + priceAfterDiscount,
    //   };
    // } else {
    //   addedItem.quantity = 1;
    //   //calculating the total
    //   let newTotal = state.total + priceAfterDiscount;

    return {
      ...state,
      addedItems: action.addedItems,
      total: newTotal,
    };
  }
  if (action.type === REMOVE_ITEM) {
    let itemToRemove = state.addedItems.find((item) => action.id === item.id);
    let new_items = state.addedItems.filter((item) => action.id !== item.id);
    let priceAfterDiscount = itemToRemove.price;
    if (itemToRemove.activeDiscount) {
      priceAfterDiscount =
        itemToRemove.price - (itemToRemove.price * itemToRemove.discount) / 100;
    }
    //calculating the total
    let newTotal = state.total - priceAfterDiscount * itemToRemove.quantity;
    console.log(itemToRemove);
    return {
      ...state,
      addedItems: new_items,
      total: newTotal,
    };
  }
  //INSIDE CART COMPONENT
  if (action.type === ADD_QUANTITY) {
    let addedItem = state.items.find((item) => item.id === action.id);
    addedItem.quantity += 1;
    let priceAfterDiscount = addedItem.price;
    if (addedItem.activeDiscount) {
      priceAfterDiscount =
        addedItem.price - (addedItem.price * addedItem.discount) / 100;
    }
    let newTotal = state.total + priceAfterDiscount;
    return {
      ...state,
      total: newTotal,
    };
  }
  if (action.type === SUB_QUANTITY) {
    let addedItem = state.items.find((item) => item.id === action.id);
    //if the qt == 0 then it should be removed
    let priceAfterDiscount = addedItem.price;
    if (addedItem.activeDiscount) {
      priceAfterDiscount =
        addedItem.price - (addedItem.price * addedItem.discount) / 100;
    }
    if (addedItem.quantity === 1) {
      let new_items = state.addedItems.filter((item) => item.id !== action.id);

      let newTotal = state.total - priceAfterDiscount;
      return {
        ...state,
        addedItems: new_items,
        total: newTotal,
      };
    } else {
      addedItem.quantity -= 1;
      let newTotal = state.total - priceAfterDiscount;
      return {
        ...state,
        total: newTotal,
      };
    }
  }

  if (action.type === ADD_SHIPPING) {
    return {
      ...state,
      total: state.total + 6,
    };
  }

  if (action.type === "SUB_SHIPPING") {
    return {
      ...state,
      total: state.total - 6,
    };
  } else {
    return state;
  }
};

export default cartReducer;
