import {
  ADD_TO_CART,
  REMOVE_ITEM,
  SUB_QUANTITY,
  ADD_QUANTITY,
  ADD_SHIPPING,
  CHANGE_FILTER,
  ON_INIT_FUNCTION,
} from "./action-types/cart-actions";

//add cart action
export const initFunction = () => {
  return async (dispatch, getState) => {
    const state = getState();

    dispatch({
      type: ON_INIT_FUNCTION,
    });
  };
};
export const changeFilter = (value, selectedValue) => {
  return async (dispatch, getState) => {
    const state = getState();
    let filteredProducts = [];

    if (value.length > 0 || selectedValue.length > 0) {
      filteredProducts = state.items.filter((each) => {
        if (selectedValue !== "select_Department") {
          return each.title.includes(value) && each.dep.includes(selectedValue);
        } else {
          return each.title.includes(value);
        }
      });
    } else {
      filteredProducts = state.items;
    }

    dispatch({
      type: CHANGE_FILTER,
      filteredProducts,
    });
  };
};
export const addToCart = (id) => {
  return async (dispatch, getState) => {
    const state = getState();
    let addedItem = state.items.find((item) => item.id === action.id);
    //check if the action id exists in the addedItems
    let existed_item = state.addedItems.find((item) => action.id === item.id);
    let priceAfterDiscount = addedItem.price;
    if (addedItem.activeDiscount) {
      priceAfterDiscount =
        addedItem.price - (addedItem.price * addedItem.discount) / 100;
    }
    if (existed_item) {
      addedItem.quantity += 1;
      return {
        ...state,
        total: state.total + priceAfterDiscount,
      };
    } else {
      addedItem.quantity = 1;
      //calculating the total
      let newTotal = state.total + priceAfterDiscount;

      dispatch({
        type: ADD_TO_CART,
        addedItems: [...state.addedItems, addedItem],
        total: newTotal,
      });
    }
  };
};
//remove item action
export const removeItem = (id) => {
  return {
    type: REMOVE_ITEM,
    id,
  };
};
//subtract qt action
export const subtractQuantity = (id) => {
  return {
    type: SUB_QUANTITY,
    id,
  };
};
//add qt action
export const addQuantity = (id) => {
  return {
    type: ADD_QUANTITY,
    id,
  };
};
