import peproni from "../../images/peproni.jpg";
import margherita from "../../images/margherita.jpg";
import chicken from "../../images/chicken.jpg";
import hawaiian from "../../images/hawaiian.jpg";
import cheeselovers from "../../images/cheeselovers.jpg";
import chili from "../../images/chili.jpg";
import {
  CHANGE_FILTER,
  ADD_PIZZA,
  EDIT_PIZZA,
  DELETE_PIZZA,
  ON_INIT_FUNCTION,
} from "../actions/action-types/cart-actions";

const initState = {
  items: [
    {
      id: 1,
      activeDiscount: true,
      discount: 5,
      title: "Pepperoni",
      desc: "we just having nice pizza with peproni and cheese",
      price: 110,
      img: peproni,
      dep: "main course",
    },
    {
      id: 2,
      activeDiscount: false,
      title: "Margherita",
      discount: 10,
      desc: "it's just a nomral pizza ",
      price: 80,
      img: margherita,
      dep: "main course",
    },
    {
      id: 3,
      activeDiscount: true,
      discount: 10,
      title: "BBQ Chicken Pizza",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 120,
      img: chicken,
      dep: "side course",
    },
    {
      id: 4,
      activeDiscount: false,
      discount: 10,
      title: "Hawaiian Pizza",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 260,
      img: hawaiian,
      dep: "side course",
    },
    {
      id: 5,
      activeDiscount: true,
      discount: 10,
      title: "Cheese Lover's",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 160,
      img: cheeselovers,
      dep: "side course",
    },
    {
      id: 6,
      activeDiscount: false,
      discount: 10,
      title: "Chilli Pizza",
      desc:
        "Lorem ipsum dolor sit amet  abc consectetur adipisicing elit. Minima, ex.",
      price: 90,
      img: chili,
      dep: "main course",
    },
    {
      id: 7,
      activeDiscount: false,
      discount: 10,
      title: "bluesss2",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
      price: 90,
      img: Item6,
      dep: "side course",
    },
  ],
  addedItems: [],
  fitlteredItems: [],
  total: 0,
};
const cartReducer = (state = initState, action) => {
  switch (action.type) {
    case ON_INIT_FUNCTION:
      return {
        ...state,
        items: state.items,
      };

    case CHANGE_FILTER:
      return {
        ...state,
        fitlteredItems: action.filteredProducts,
      };
    case ADD_PIZZA: {
      return {
        ...state,
        items: action.items,
      };
    }
    case EDIT_PIZZA: {
      return {
        ...state,
        items: action.items,
      };
    }
    case DELETE_PIZZA: {
      return { ...state, items: action.items };
    }

    default:
      return state;
  }
};

export default cartReducer;
