import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import "./Cart.css";
import {
  removeItem,
  addQuantity,
  subtractQuantity,
  addPizza,
} from "../actions/cartActions";

class Cart extends Component {
  constructor() {
    super();
    this.state = {
      type: "",
      name: "",
      price: "",
      photo: "",
    };
    this.handelChange = this.handelChange.bind(this);
  }
  handelChange(e) {
    let change = {};
    change[e.target.name] = e.target.value;
    this.setState(change);
  }
  componentDidMount() {
    if (typeof this.props.match.params.id == "undefined") {
      console.log("adding new one");
    } else {
      const editedId = this.props.match.params.id;

      let editableItem = this.props.items.find((item) => item.id == editedId);
      console.log(editableItem);
      this.setState({
        type: editableItem.type,
        title: editableItem.title,
        price: editableItem.price,
        photo: editableItem.photo,
      });
    }
  }
  render() {
    return (
      <div className="container">
        <form
          onSubmit={(e) => {
            // console.log(this.state, "this.state");
            e.preventDefault();
            this.props.addPizza(this.state);
            this.props.history.push("/");
          }}
        >
          <h3>Sign In</h3>
          <center>
            <h6 style={{ color: "red" }}>{this.props.errorMessage}</h6>
          </center>

          <div className="form-group">
            <div className="mainFormDiv">
              <div className="formTitle">
                <label>Type</label>
              </div>
              <div className="formInput">
                <select
                  name="type"
                  onChange={this.handelChange}
                  value={this.state.type}
                >
                  <option value="select_Department">Select Course Type</option>
                  <option value="main course">main course</option>
                  <option value="side course">side couse</option>
                </select>
              </div>
            </div>
          </div>
          <div className="mainFormDiv">
            <div className="formTitle">
              <label>Name</label>
            </div>
            <div className="formInput">
              <input
                name="name"
                placeholder="pizza name"
                value={this.state.name}
                onChange={this.handelChange}
              />
            </div>
          </div>

          <div className="mainFormDiv">
            <div className="formTitle">
              <label>Price</label>
            </div>
            <div className="formInput">
              <input
                name="price"
                placeholder="Price"
                value={this.state.price}
                onChange={this.handelChange}
              />
            </div>
          </div>

          <div className="mainFormDiv">
            <button type="submit" className="waves-effect waves-light btn">
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    items: state.items,
    //addedItems: state.addedItems
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    addPizza: (formValues) => {
      dispatch(addPizza(formValues));
    },
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Cart);
