import React, { Component } from "react";
import { connect } from "react-redux";
// import { addToCart, changeFilter } from "./actions/cartActions";
import * as actions from "./actions/cartActions";

class Pagination extends React.Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      currentPage: null,
      pageCount: null,
    };
  }

  componentWillMount() {
    const startingPage = this.props.startingPage ? this.props.startingPage : 1;
    const data = this.props.data;
    const pageSize = this.props.pageSize;
    let pageCount = parseInt(data.length / pageSize);
    if (data.length % pageSize > 0) {
      pageCount++;
    }
    this.setState({
      currentPage: startingPage,
      pageCount: pageCount,
    });
  }

  setCurrentPage(num) {
    this.setState({ currentPage: num });
  }

  createControls() {
    let controls = [];
    const pageCount = this.state.pageCount;
    for (let i = 1; i <= pageCount; i++) {
      const baseClassName = "pagination-controls__button";
      const activeClassName =
        i === this.state.currentPage ? `${baseClassName}--active` : "";
      controls.push(
        <div
          className={`${baseClassName} ${activeClassName}`}
          onClick={() => this.setCurrentPage(i)}
        >
          {i}
        </div>
      );
    }
    return controls;
  }

  createPaginatedData() {
    const data = this.props.data;
    const pageSize = this.props.pageSize;
    const currentPage = this.state.currentPage;
    const upperLimit = currentPage * pageSize;
    const dataSlice = data.slice(upperLimit - pageSize, upperLimit);
    return dataSlice;
  }

  render() {
    return (
      <div className="pagination">
        <div className="pagination-controls">{this.createControls()}</div>
        <div className="pagination-results">
          {React.cloneElement(this.props.children, {
            data: this.createPaginatedData(),
          })}
        </div>
      </div>
    );
  }
}

class Example extends React.Component {
  render() {
    const data = this.props.data;
    return (
      <div className="example">
        {data.map((item) => {
          return (
            <div className="example__item">
              {item.id} {item.first_name} {item.last_name}
            </div>
          );
        })}
      </div>
    );
  }
}
class Home extends Component {
  constructor(props) {
    super(props);
    var exampleItems = this.props.items;

    this.state = {
      inputValue: "",
      depSelectedValue: "select_Department",
      exampleItems: exampleItems,
      pageOfItems: [],
    };
    this.onChangePage = this.onChangePage.bind(this);
  }
  onChangePage(pageOfItems) {
    // update state with new page of items
    this.setState({ pageOfItems: pageOfItems });
  }
  componentDidMount() {
    this.props.onInitFunction("");
  }
  handleClick = (id) => {
    this.props.addToCart(id);
  };
  handleDepChange = (value) => {
    let inputValuee = value.target.value;
    this.setState({ depSelectedValue: inputValuee });

    this.props.onChangeFilter(this.state.inputValue, inputValuee);
  };
  handleInputChange = (value) => {
    let inputValueq = value.target.value;
    this.setState({ inputValue: inputValueq });

    this.props.onChangeFilter(inputValueq, this.state.depSelectedValue);
  };
  render() {
    let itemList = this.state.pageOfItems.map((item) => {
      return (
        <div key={item.id}>
          <div className="card" key={item.id}>
            <div className="card-image">
              <img src={item.img} alt={item.title} />
              <span className="card-title">{item.title}</span>
              <span
                to="/"
                className="btn-floating halfway-fab waves-effect waves-light red"
                onClick={() => {
                  this.handleClick(item.id);
                }}
              >
                <i className="material-icons">add</i>
              </span>
            </div>

            <div className="card-content">
              <p>{item.desc}</p>
              <p>
                <b>
                  Price:{" "}
                  {item.activeDiscount ? (
                    <span>
                      <span
                        style={{
                          textDecorationLine: "line-through",
                          textDecorationStyle: "solid",
                        }}
                      >
                        {item.price}$
                      </span>
                      =>
                      {Math.round(item.price) -
                        (item.price * item.discount) / 100}
                      $
                    </span>
                  ) : (
                    item.price + "$"
                  )}
                </b>
              </p>
            </div>
          </div>
        </div>
      );
    });

    return (
      <div className="container">
        <div className="search">
          <div className="searchFor">
            <input
              className="input_field"
              type="text"
              placeholder="search For Product"
              onChange={this.handleInputChange}
              value={this.state.inputValue}
            />
          </div>
          <div>
            <select
              className="input_field"
              onChange={this.handleDepChange}
              value={this.state.depSelectedValue}
            >
              <option value="select_Department">Select a Department</option>
              <option value="cars">cars</option>
              <option value="shoes">shoes</option>
            </select>
          </div>
        </div>
        <h3 className="center">Our items</h3>

        <div className="box">{itemList}</div>
        <center>
          <div className="text-center">
            <Pagination
              data={
                this.state.inputValue.length > 0 ||
                this.state.depSelectedValue !== "select_Department"
                  ? this.props.fitlteredItems
                  : this.props.items
              }
            >
              <Example />
            </Pagination>
            <Pagination
              items={
                this.state.inputValue.length > 0 ||
                this.state.depSelectedValue !== "select_Department"
                  ? this.props.fitlteredItems
                  : this.props.items
              }
              onChangePage={this.onChangePage}
            />
          </div>
        </center>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    items: state.items,
    fitlteredItems: state.fitlteredItems,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    onInitFunction: (mainObject) => dispatch(actions.initFunction(mainObject)),
    addToCart: (id) => {
      dispatch(actions.addToCart(id));
    },
    onChangeFilter: (inputValue, selectedValue) =>
      dispatch(actions.changeFilter(inputValue, selectedValue)),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Home);
